class CacheManager : ICache {

    private val dao = AppDatabase.getInstance().cacheDao()

    companion object {
        const val DEFAULT_CACHE_LIMIT = 8 * AlarmManager.INTERVAL_HOUR
    }

    override suspend fun saveCache(key: String, cacheData: String) {
        val item = CacheBean()
        item.cacheKey = key
        item.cacheContent = cacheData
        item.cacheTime = System.currentTimeMillis()
        item.cacheLimit = DEFAULT_CACHE_LIMIT
        dao.addCache(item)
    }

    override suspend fun loadCache(key: String): CacheBean? {
        val list = dao.loadCache(key)
        return if (list.isNotEmpty()) {
            list.first()
        } else {
            null
        }
    }


    override suspend fun clearCache(key: String) {
        dao.removeCacheByKey(key)
    }

    override suspend fun isCacheExist(key: String): Boolean {
        return dao.loadCache(key).isNotEmpty()
    }
}